// Helper function to get icon with custom size
Icons.get = function(name, size = 20) {
    const icon = this[name];
    if (!icon) {
        console.warn(`Icon "${name}" not found`);
        return '';
    }
    return icon.replace(/width="20"/g, `width="${size}"`).replace(/height="20"/g, `height="${size}"`);
};

// Helper function to get icon as inline element
Icons.inline = function(name, className = '') {
    const icon = this[name];
    if (!icon) return '';
    return `<span class="icon ${className}">${icon}</span>`;
};

// Make available globally
if (typeof window !== 'undefined') {
    window.Icons = Icons;
}

// Export for module use
if (typeof module !== 'undefined' && module.exports) {
    module.exports = Icons;
}
